<?phpclass FaqsController extends FaqAppController {    /**     * Controller name     *     * @var string     */    var $name = 'Faqs';    public $helpers = array('Html', 'Form', 'Session', 'Time', 'Text');    public $components = array('Auth', 'Session', 'Email', 'Cookie', 'Search.Prg', 'RequestHandler');    public $presetVars = array(array('field' => 'email', 'type' => 'value'), array('field' => 'firstname', 'type' => 'value'), array('field' => 'username', 'type' => 'value'), array('field' => 'mobile', 'type' => 'value'), array('field' => 'user_role_id', 'type' => 'value'), array('field' => 'status', 'type' => 'value'));    //public $presetVars = 	true;    public function beforeFilter() {        parent::beforeFilter();        $this->set('model', $this->modelClass);        $this->set('tab_open', 'faqs');        $this->loadModel("Menu");        $cat_list = $this->Menu->find("list", array(            'conditions' => array(                'Menu.is_active' => 1,                'Menu.is_faqs' => 1,            ),            'joins' => array(                array(                    'table' => 'pages',                    'alias' => 'Page',                    'type' => 'LEFT',                    'conditions' => array(                        'Menu.page_id = Page.id'                    )                )            ),            'fields' => array("Menu.slug", "Menu.name"),                )        );        $this->set('pos_list', $cat_list);    }    public function index() {        $conditions = array();        $conditions = array();        $limitValue = $limit = 25;        $page = ((isset($this->params->named['page']) && $this->params->named['page'] != "") ? $this->params->named['page'] : 0);        $this->set('limitValue', $limitValue);        $this->set('limit', $limit);        $this->set('page', $page);        if ($this->request->is('get') && !isset($this->request->data['recordsPerPage'])) {            if (!empty($this->request->query)) {                if (isset($this->request->query['menu_slug']) && $this->request->query['menu_slug'] != "") {                    array_push($conditions, array('Faq.menu_slug' => $this->request->query['menu_slug']));                    $this->set("menu_slug", $this->request->query['menu_slug']);                }                if (isset($this->request->query['is_active']) && $this->request->query['is_active'] != "") {                    array_push($conditions, array('Faq.is_active' => $this->request->query['is_active']));                    $this->set("is_active", $this->request->query['is_active']);                }                              if (@$this->request->query['from_date'] and @ $this->request->query['to_date']) {                    array_push($conditions, array('(DATE_FORMAT(Faq.created_on, "%Y-%m-%d") >= ? AND DATE_FORMAT(Faq.created_on, "%Y-%m-%d") <= ?)' => array($this->request->query['from_date'], $this->request->query['to_date'])));                } else                if (@$this->request->query['from_date']) {                    array_push($conditions, array('DATE_FORMAT(Faq.created_on, "%Y-%m-%d") >= ' => $this->request->query['from_date']));                } else                if (@$this->request->query['to_date']) {                    array_push($conditions, array('DATE_FORMAT(Faq.created_on, "%Y-%m-%d") <= ' => $this->request->query['to_date']));                }                $this->set("from_date", @$this->request->query['from_date']);                $this->set("to_date", @$this->request->query['to_date']);            }        }        $this->paginate = array(            'conditions' => $conditions,            'limit' => 25,            'joins' => array(                array(                    'table' => 'menus',                    'alias' => 'Menu',                    'type' => 'LEFT',                    'conditions' => array(                        'Menu.slug = Faq.menu_slug'                    )                )            ),            'fields'=>array("Faq.*","Menu.name"),            'order' => array(                'Faq.created_on' => 'DESC'            )        );        //pr($this->paginate('Faq'));        $this->set('records', $this->paginate('Faq'));        $this->set('title_for_layout', "FAQ's");    }///$this->{$this->modelClass}->set($data_chk);    public function add() {        if ($this->request->is('post')) {            $this->request->data['Faq']['created_on'] = date("Y-m-d H:i:s");            $this->request->data['Faq']['is_active'] = 1;            if ($this->Faq->save($this->request->data)) {                $new_id = $this->{$this->modelClass}->id;                $json_data = json_encode($this->request->data);                $text_action = "Added";                $this->global_logs("faqs", $new_id, 0, $text_action, $json_data);                $this->Session->setFlash(__('Faq added successfully'), 'success');                $this->redirect(array('plugin' => 'faq', 'controller' => 'faqs', 'action' => 'index'));            }        }        $this->set('title_for_layout', 'Add New FAQ');    }    public function edit() {        $user_id = $this->params->query['id'];        $conditions = array(            "Faq.id" => $user_id,        );        if (!$user_id || $user_id == NULL) {            $this->Session->setFlash('Invalid request to edit Faq', 'error');            $this->redirect(array('plugin' => 'faq', 'controller' => 'faqs', 'action' => 'index'));        } else {// check that user exists or not            $check_user_exists = $this->Faq->Find('count', array('conditions' => $conditions, 'recursive' => -1));            if ($check_user_exists == 0) {                $this->Session->setFlash('Faq does not exists', 'error');                $this->redirect(array('plugin' => 'faq', 'controller' => 'faqs', 'action' => 'index'));            }        }        $users_data = $this->Faq->find('first', array('conditions' => $conditions));        $this->set('users_data', $users_data);        if ($this->request->is('post') || $this->request->is('put')) {            if ($this->Faq->save($this->request->data)) {                $json_data = json_encode($this->request->data);                $text_action = "updated";                $this->global_logs("faqs", $user_id, 1, $text_action, $json_data);                $this->Session->setFlash(__('FAQ updated successfully'), 'success');                $this->redirect(array('plugin' => 'faq', 'controller' => 'faqs', 'action' => 'index'));            } else {                $this->Session->setFlash('Faq couldn\'t be updated, try again later', 'error');            }        } else {            $this->data = $users_data;        }        $this->set('title_for_layout', 'Update FAQ');    }    public function status_ajax() {        if ($this->request->is('Ajax')) {            if ($this->data['id'] != null) {                $all_data = $this->{$this->modelClass}->findById($this->data['id']);                if ($all_data[$this->modelClass]['is_active'] == 1) {                    $new_value = 0;                    $data['is_active'] = 0;                    $status = 3;                    $text_action = " inactivated";                } else {                    $new_value = 1;                    $data['is_active'] = 1;                    $status = 2;                    $text_action = " activated";                }                $this->{$this->modelClass}->id = $this->data['id'];                $this->{$this->modelClass}->save($data, false);                $json_data = json_encode($data);                $this->global_logs("faqs", $this->data['id'], $status, $text_action, $json_data);                echo 1;            }        }        exit;    }}