<?phpclass ScoresController extends ScoreAppController {    /**     * Controller name     *     * @var string     */    var $name = 'Scores';    public $helpers = array('Html', 'Form', 'Session', 'Time', 'Text');    public $components = array('Auth', 'Session', 'Email', 'Cookie', 'Search.Prg', 'RequestHandler');    public $presetVars = array(array('field' => 'email', 'type' => 'value'), array('field' => 'firstname', 'type' => 'value'), array('field' => 'username', 'type' => 'value'), array('field' => 'mobile', 'type' => 'value'), array('field' => 'user_role_id', 'type' => 'value'), array('field' => 'status', 'type' => 'value'));    //public $presetVars = 	true;    public function beforeFilter() {        parent::beforeFilter();        $this->set('model', $this->modelClass);        $this->set('tab_open', 'scores');        $this->loadModel("Course");        $cat_list = $this->Course->find("list", array('conditions' => array(                'is_course' => 1        )));        $this->set("cat_list", $cat_list);    }    public function index() {        $conditions = array();        $conditions = array();        $limitValue = $limit = 25;        $page = ((isset($this->params->named['page']) && $this->params->named['page'] != "") ? $this->params->named['page'] : 0);        $this->set('limitValue', $limitValue);        $this->set('limit', $limit);        $this->set('page', $page);        if ($this->request->is('get') && !isset($this->request->data['recordsPerPage'])) {            if (!empty($this->request->query)) {                if (isset($this->request->query['course_id']) && $this->request->query['course_id'] != "") {                    array_push($conditions, array('Score.course_id' => $this->request->query['course_id']));                    $this->set("course_id", $this->request->query['course_id']);                }                if (isset($this->request->query['is_active']) && $this->request->query['is_active'] != "") {                    array_push($conditions, array('Score.is_active' => $this->request->query['is_active']));                    $this->set("is_active", $this->request->query['is_active']);                }                if (isset($this->request->query['name']) && $this->request->query['name'] != "") {                    array_push($conditions, array('Score.name LIKE' => "%" . $this->request->query['name'] . "%"));                    $this->set("name", $this->request->query['name']);                }                if (@$this->request->query['from_date'] and @ $this->request->query['to_date']) {                    array_push($conditions, array('(DATE_FORMAT(Score.created_on, "%Y-%m-%d") >= ? AND DATE_FORMAT(Score.created_on, "%Y-%m-%d") <= ?)' => array($this->request->query['from_date'], $this->request->query['to_date'])));                } else                if (@$this->request->query['from_date']) {                    array_push($conditions, array('DATE_FORMAT(Score.created_on, "%Y-%m-%d") >= ' => $this->request->query['from_date']));                } else                if (@$this->request->query['to_date']) {                    array_push($conditions, array('DATE_FORMAT(Score.created_on, "%Y-%m-%d") <= ' => $this->request->query['to_date']));                }                $this->set("from_date", @$this->request->query['from_date']);                $this->set("to_date", @$this->request->query['to_date']);            }        }        $this->paginate = array(            'conditions' => $conditions,            'limit' => 25,            'order' => array(                'Score.created_on' => 'DESC'            )        );        $this->set('records', $this->paginate('Score'));        $this->set('title_for_layout', 'Student Scores');    }///$this->{$this->modelClass}->set($data_chk);    public function add() {        if ($this->request->is('post')) {            $this->request->data['Score']['created_on'] = date("Y-m-d H:i:s");            $this->request->data['Score']['is_active'] = 1;            $is_success = 1;            $upload_image_folder = STUDENTS_IMAGE_PATH;            $filename = $this->data{$this->modelClass}['image']['name'];            if (isset($this->request->data{$this->modelClass}['image']) && $this->request->data{$this->modelClass}['image']['name'] != "") {                $allowed_extensions = array('jpg', 'JPG', 'jpeg', 'png', 'gif');                $uploaded_image = $this->request->data{$this->modelClass}['image']['name'];                $imgExtension = pathinfo($uploaded_image, PATHINFO_EXTENSION);                $uploadimageArray = $this->request->data{$this->modelClass}['image'];                if (!in_array($imgExtension, $allowed_extensions)) {                    $this->data = $this->request->data;                    $is_success = 0;                    $this->Session->setFlash(__('Image extension should be of ' . implode(",", $allowed_extensions) . ' only'), 'error');                } else {                    if (isset($uploadimageArray) && $uploadimageArray['name'] != "") {                        $file_name = basename($uploadimageArray['name']);                        $imgExtension = pathinfo($file_name, PATHINFO_EXTENSION);                        $image_name = explode("." . $imgExtension, $file_name);                        $prefix = time() . "~@~";                        $file_name = base64_encode($prefix . $image_name[0]) . "." . $imgExtension;                        if (move_uploaded_file($uploadimageArray['tmp_name'], $upload_image_folder . DS . $file_name)) {                            $this->request->data['Score']['image'] = $file_name;                        }                    }                }                //unset($this->request->data{$this->modelClass}['image']);            } else {                unset($this->request->data{$this->modelClass}['image']);            }            if ($is_success == 1) {                if ($this->Score->save($this->request->data)) {                    $new_id = $this->{$this->modelClass}->id;                    $json_data = json_encode($this->request->data);                    $text_action = "Added";                    $this->global_logs("scores", $new_id, 0, $text_action, $json_data);                    $this->Session->setFlash(__('Student Score added successfully'), 'success');                    $this->redirect(array('plugin' => 'score', 'controller' => 'scores', 'action' => 'index'));                }            }        }        $this->set('title_for_layout', 'Add New Student Score');    }    function download_format() {        $this->download("student_result_format.csv", 'result_format');    }    public function upload_csv() {        if ($this->request->is('post')) {            if (isset($this->request->data['Score']['csv_file']) && $this->request->data['Score']['csv_file']['name'] != "") {                $allowed_extensions = array('csv');                $uploaded_image = $this->request->data['Score']['csv_file']['name'];                $imgExtension = pathinfo($uploaded_image, PATHINFO_EXTENSION);                if (!in_array($imgExtension, $allowed_extensions)) {                    $this->data = $this->request->data;                    $is_success = 0;                    $this->Session->setFlash(__('File card extension should be of ' . implode(",", $allowed_extensions) . ' only'), 'error');                }                $FILE = $this->request->data['Score']['csv_file'];                unset($this->request->data['Score']['csv_file']);            } else {                unset($this->request->data['Score']['csv_file']);                $this->Session->setFlash('Please select file.', 'error');                $this->redirect(array('plugin' => 'score', 'controller' => 'scores', 'action' => 'upload_csv'));            }            $upload_folder = IMPORTS_WBROOT_PATH;            if (isset($FILE) && $FILE['name'] != "") {                $file_act_name = $FILE['name'];                $file_name = date("Y-m-d H:i:s") . '_' . basename($FILE['name']);                $file_name = str_replace(array(" ", "-", ":"), array("_", "_", "_"), $file_name);                if (move_uploaded_file($FILE['tmp_name'], $upload_folder . $file_name)) {                    $fileLoc = $upload_folder . $file_name;                    $saveData = array();                    $file = fopen($fileLoc, "r");                    $i = 0;                    $j = 0;                    while (!feof($file)) {                        $data = fgetcsv($file);                        if ($i > 0) {                            if ($data[0]) {                                $saveData['Score'][$j]['name'] = "$data[0]";                                $saveData['Score'][$j]['marks'] = "$data[1]";                                $saveData['Score'][$j]['pass_year'] = "$data[2]";                                $saveData['Score'][$j]['course_id'] = $this->request->data['Score']['course_id'];                                $saveData['Score'][$j]['created_on'] = date("Y-m-d H:i:s");                                $j++;                            }                        }                        $i++;                    }                    fclose($file);                    //pr($saveData['Score']);                    if (!empty($saveData)) {                        $this->Score->saveAll($saveData['Score']);                    }                    $new_id = $this->request->data['Score']['course_id'];                    $json_data = json_encode($saveData);                    $text_action = "Added";                    $this->global_logs("scores", $new_id, 0, $text_action, $json_data);                    $this->Session->setFlash(__('Student Score added successfully'), 'success');                    $this->redirect(array('plugin' => 'score', 'controller' => 'scores', 'action' => 'index'));                } else {                    $this->Session->setFlash('Enable to uplode file.', 'error');                    $this->redirect(array('plugin' => 'score', 'controller' => 'scores', 'action' => 'upload_csv'));                }            }        }        $this->set('title_for_layout', 'Upload Student Scores');    }    public function edit() {        $user_id = $this->params->query['id'];        $conditions = array(            "Score.id" => $user_id,        );        if (!$user_id || $user_id == NULL) {            $this->Session->setFlash('Invalid request to edit Score', 'error');            $this->redirect(array('plugin' => 'score', 'controller' => 'scores', 'action' => 'index'));        } else {// check that user exists or not            $check_user_exists = $this->Score->Find('count', array('conditions' => $conditions, 'recursive' => -1));            if ($check_user_exists == 0) {                $this->Session->setFlash('Score does not exists', 'error');                $this->redirect(array('plugin' => 'score', 'controller' => 'scores', 'action' => 'index'));            }        }        $users_data = $this->Score->find('first', array('conditions' => $conditions));        $this->set('users_data', $users_data);        if ($this->request->is('post') || $this->request->is('put')) {            $upload_image_folder = STUDENTS_IMAGE_PATH;            $filename = $this->data{$this->modelClass}['url_image']['name'];            if (isset($this->request->data{$this->modelClass}['url_image']) && $this->request->data{$this->modelClass}['url_image']['name'] != "") {                $this->request->data['Score']['image'] = $users_data['Score']['image'];                $allowed_extensions = array('jpg', 'JPG', 'jpeg', 'png', 'gif');                $uploaded_image = $this->request->data{$this->modelClass}['url_image']['name'];                $imgExtension = pathinfo($uploaded_image, PATHINFO_EXTENSION);                $uploadimageArray = $this->request->data{$this->modelClass}['url_image'];                if (!in_array($imgExtension, $allowed_extensions)) {                    $this->data = $this->request->data;                    $is_success = 0;                    $this->Session->setFlash(__('Image extension should be of ' . implode(",", $allowed_extensions) . ' only'), 'error');                } else {                    if (isset($uploadimageArray) && $uploadimageArray['name'] != "") {                        $file_name = basename($uploadimageArray['name']);                        $imgExtension = pathinfo($file_name, PATHINFO_EXTENSION);                        $image_name = explode("." . $imgExtension, $file_name);                        $prefix = time() . "~@~";                        $file_name = base64_encode($prefix . $image_name[0]) . "." . $imgExtension;                        if (move_uploaded_file($uploadimageArray['tmp_name'], $upload_image_folder . DS . $file_name)) {                            $this->request->data['Score']['image'] = $file_name;                            $image = $users_data['Score']['image'];                            if ($image) {                                if (file_exists(STUDENTS_IMAGE_PATH . $image)) {                                    @unlink(STUDENTS_IMAGE_PATH . $image);                                }                            }                        }                    }                }                unset($this->request->data{$this->modelClass}['url_image']);            } else {                $this->request->data['Score']['image'] = $users_data['Score']['image'];                unset($this->request->data{$this->modelClass}['url_image']);            }            if ($this->Score->save($this->request->data)) {                $json_data = json_encode($this->request->data);                $text_action = "updated";                $this->global_logs("scores", $user_id, 1, $text_action, $json_data);                $this->Session->setFlash(__('Student Score updated successfully'), 'success');                $this->redirect(array('plugin' => 'score', 'controller' => 'scores', 'action' => 'index'));            } else {                $this->Session->setFlash('Score couldn\'t be updated, try again later', 'error');            }        } else {            $this->data = $users_data;        }        $this->set('title_for_layout', 'Update Student Score');    }    public function status_ajax() {        if ($this->request->is('Ajax')) {            if ($this->data['id'] != null) {                $all_data = $this->{$this->modelClass}->findById($this->data['id']);                if ($all_data[$this->modelClass]['is_active'] == 1) {                    $new_value = 0;                    $data['is_active'] = 0;                    $status = 3;                    $text_action = " inactivated";                } else {                    $new_value = 1;                    $data['is_active'] = 1;                    $status = 2;                    $text_action = " activated";                }                $this->{$this->modelClass}->id = $this->data['id'];                $this->{$this->modelClass}->save($data, false);                $json_data = json_encode($data);                $this->global_logs("scores", $this->data['id'], $status, $text_action, $json_data);                echo 1;            }        }        exit;    }}